import { Component } from "@angular/core";
import { LoadingController, ModalController } from "@ionic/angular";
import { Bet, BettingService, Draw } from "../services/betting.service";
import { WalletService } from "../services/wallet.service";
import { ToastController } from '@ionic/angular';
import { Printer, PrintOptions } from '@awesome-cordova-plugins/printer/ngx';
import * as QRCodeLib from 'qrcode';
import { DrawCategories, SessionService } from "../services/session.service";
import { BluetoothPrinterService } from "../services/bluetooth-printer.service";
import { take } from "rxjs/operators";
//import "../../assets/js-escpos-builder-master/dist/JSESCPOSBuilder";
//import { JSPrintManager } from "jsprintmanager";
import * as domtoimage from 'dom-to-image'

@Component({
    selector: 'betting-form-modal',
    templateUrl: 'betting-form-modal.component.html'
})

export class BettingFormModal {

    loading = null
    is_send_button_disabled: boolean = true
    is_print_button_disabled: boolean = false
    draw_category: DrawCategories = 1
    straight: number = null
    ramble: number = null
    number: string 
    draw: Draw = null
    cost: number = 0
    wallet_balance: number 
    bets: Array<Bet> = []
    bets_print_cache: Array<Bet> = []
    // used for 3d place bets
    date_string: string = "-"
    time_string: string = "="
    ctr_3d: string = "A"

    constructor(
        private bprinter: BluetoothPrinterService,
        private session_service: SessionService,
        private betting_service: BettingService,
        private wallet_service: WalletService,
        private loadingController: LoadingController,
        private printer: Printer,
        public modalController: ModalController,
        public toastController: ToastController
    
    ) {
        this.wallet_service.get_wallet().subscribe(data => {
            this.wallet_balance = data
            console.log(data)
        })

        this.betting_service.active_draw(this.draw_category).pipe(take(1)).subscribe(active_draw => {
            this.draw = active_draw
        })

    }

    async presentLoadingWithOptions() {
        this.loading = await this.loadingController.create({
            message: 'Communicating with Server',
            translucent: true,
            cssClass: 'custom-class custom-loading',
            backdropDismiss: false,
            showBackdrop: true,
            animated: true,
            mode: 'md',
            keyboardClose: true,
            duration: 8000
        });

        await this.loading.present();
    
        // const { role, data } = await this.loading.onDidDismiss();
        // console.log('Loading dismissed with role:', role);
    }

    async presentToastWithOptions(header: string, message: string, color: string) {
        const toast = await this.toastController.create({
          header: header,
          message: message,
          duration: 7000,
          position: 'bottom',
          color: color,
          buttons: [
            {
              text: 'OK',
              role: 'cancel',
              handler: () => {
                console.log('Cancel clicked');
              }
            }
          ]
        });
        await toast.present();
    
        const { role } = await toast.onDidDismiss();
        console.log('onDidDismiss resolved with role', role);
    }

    set_draw_category(category) {
        if(category === '3d') {
            this.draw_category = DrawCategories["3d"]
        }

        if(category === 'stl') {
            this.draw_category = DrawCategories.stl
        }
        this.betting_service.active_draw(this.draw_category).pipe(take(1)).subscribe(active_draw => {
            this.draw = active_draw
            
            this.bets.forEach(bet => {
                bet.draw = active_draw
            })
        })
    }
    
    print(draw_category: DrawCategories, bets: Bet[]) {

        // let a = new Date()
        // this.date_string = a.getFullYear() + `-` + (a.getMonth() + 1) + `-` + a.getDate()
        // this.time_string = this.draw.to.toLocaleString('en-US', { hour: 'numeric', minute:'2-digit', hour12: true }).replace(" ","")       
        // this.ctr_3d = this.betting_service.get_3d_ctr()
        // this.betting_service.increment_3d_ctr()
        // this.bets_print_cache = bets
        
        //  return
        let list_bet = bets
        this.bets_print_cache = bets
        this.bprinter.disconnectBluetoothPrinter()
        
        let escpos = Neodynamic.JSESCPOSBuilder;
        let doc = new escpos.Document(); 
        var escposCommands: Uint8Array = null
        // var sauna

        let date = new Date()
        let months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec']
        let qrdata = "|" + this.session_service.agent_code + "|" + this.session_service.agent_name + "|00000000|34|" + date.getTime() + "|" + months[date.getMonth()] + " " + date.getDate() + ", " + date.getFullYear() + "|" + this.draw.to.toLocaleString('en-US', { hour: 'numeric', hour12: true })
 
        QRCodeLib.toDataURL(qrdata,{
            errorCorrectionLevel: "L",
            scale: 4
        }).then(qr_base64 => {

            if(draw_category == DrawCategories.stl) {

                let img = "data:image/png;base64,"      
                
                escpos.ESCPOSImage.load(qr_base64).then(qr => {
                    
                    escpos.ESCPOSImage.load(img).then(logo =>{
    
                    
                        let d = date.getFullYear() + `-` + (date.getMonth() + 1) + `-` + date.getDate()
                        let t = date.getHours() + `:` + date.getMinutes() + `:` + date.getSeconds()
                        
                        let solat = ""
        
                        for(let i=0; i< list_bet.length; i++) {
                            
                            if(list_bet[i].straight > 0) {
                                solat += list_bet[i].number + "   T     " + list_bet[i].straight + ".00\n"
                            }
                            
                            if(list_bet[i].ramble > 0) {
                                solat += list_bet[i].number + "   R     " + list_bet[i].ramble + ".00"
                            }
        
                            if(i + 1 < list_bet.length) {
                                solat += "\n"
                            }
        
                        }
        
                        escposCommands = doc
                            .align(escpos.TextAlignment.Center)
                            .image(logo, escpos.BitmapDensity.D24)
                            .align(escpos.TextAlignment.LeftJustification)
                            .size(0,0)     
                            .font(escpos.FontFamily.C)  
                            .text("D: " + d + " T:" + t)
                            .size(0,1)
                            .text("REF: " + this.betting_service.get_stl_ctr()) 
                            .size(0,0)
                            .text("Draw " + this.draw.to.toLocaleString('en-US', { hour: 'numeric', hour12: true }))
                            .size(0, 0)
                            .text(solat) 
                            .size(0,1)
                            .text("PHP " + this.cost) 
                            .size(0,0)
                            .text("000000" + date.getFullYear() + date.getMonth() + date.getDate() + date.getHours() + date.getMinutes() + date.getSeconds() + "\n")
                            .size(0,0)
                            .text("Machine ID : " + this.session_service.machine_id)
                            .size(0,0)
                            .text("Agent Name : " + this.session_service.agent_name)
                            .size(0,0)
                            .text(this.session_service.agent_code)
                            .feed(2)
                            .align(escpos.TextAlignment.Center)
                            .image(qr,escpos.BitmapDensity.D24)
                            .feed(2)
                            .generateUInt8Array() 
        
                        this.bprinter.sendToBluetoothPrinter(this.session_service.printer_mac_address,escposCommands)
        
                        this.betting_service.increment_stl_ctr()
                        this.bets_print_cache = null
                    })
                })
    
            }
            else if(draw_category == DrawCategories["3d"]) {


                let months = ['JAN','FEB','MAR','APR','MAY','JUN','JUL','AUG','SEP','OCT','NOV','DEC']
                let hour_now = (date.getHours() % 12 == 0?"12": date.getHours()%12)
                let ampm = (date.getHours() < 12?"AM":"PM")         

                this.date_string = months[date.getMonth()] + " " + date.getDate() + "," + date.getFullYear() + " " + String(hour_now).padStart(2,"0") + ":" + String(date.getMinutes()).padStart(2,"0") + " " + ampm

                if(this.draw.to.toLocaleString('en-US', { hour: '2-digit' }) == "11 PM") {
                    this.time_string = "9PM"
                }
                else {
                    this.time_string = this.draw.to.toLocaleString('en-US', { hour: 'numeric', hour12: true }).replace(" ","")
                }

                                        
                let solat = ""
        
                for(let i=0; i< list_bet.length; i++) {
                    
                    solat += list_bet[i].number + "            " + list_bet[i].straight + "         " + list_bet[i].ramble
                    
                    if(i + 1 < list_bet.length) {
                        solat += "\n"
                    }

                }

                //this.ctr_3d = this.betting_service.get_3d_ctr()


                //let d = date.getFullYear() + `-` + (date.getMonth() + 1) + `-` + date.getDate()
                //let t = date.getHours() + `:` + date.getMinutes() + `:` + date.getSeconds() 

                escpos.ESCPOSImage.load(qr_base64).then(qr => {

                escposCommands = doc
                    .align(escpos.TextAlignment.Center)
                    .text("3D Games")
                    .text("KD GAMING")
                    .text("V8.17")
                    .feed(1)
                    .align(escpos.TextAlignment.LeftJustification)
                    .text("Cashier Code: " + this.session_service.agent_code)
                    .text("Draw Date:" + this.date_string)
                    .text("Draw Time:" + this.time_string)
                    .text("Ref. No: " + "2311" + date.getMonth() + date.getFullYear() + date.getDate() + date.getMinutes() + date.getHours() + date.getSeconds() + "MOB")
                    .feed(2)
                    .text("Combination  Target    Rambol")
                    .text(solat)
                    .feed(1)
                    .text("Total Amount: " + this.cost) 
                    .text("Total Bets: " + list_bet.length) 
                    .text(this.session_service.code_today_3d) 
                    .align(escpos.TextAlignment.LeftJustification)
                    .image(qr,escpos.BitmapDensity.D24)
                    .feed(3)
                    // .image(logo, escpos.BitmapDensity.D24)
                    // .align(escpos.TextAlignment.LeftJustification)
                    // .size(0,0)     
                    // .font(escpos.FontFamily.C)  
                    // .text("D: " + d + " T:" + t)
                    // .size(0,1)
                    // .text("REF: " + this.betting_service.get_stl_ctr()) 
                    // .size(0,0)
                    // .text("Draw " + this.draw.to.toLocaleString('en-US', { hour: 'numeric', hour12: true }))
                    // .size(0, 0)
                    // .text(solat) 
                    // .size(0,1)
                    // .text("PHP " + this.cost) 
                    // .size(0,0)
                    // .text("000000" + date.getFullYear() + date.getMonth() + date.getDate() + date.getHours() + date.getMinutes() + date.getSeconds() + "\n")
                    // .size(0,0)
                    // .text("Machine ID : " + this.session_service.machine_id)
                    // .size(0,0)
                    // .text("Agent Name : " + this.session_service.agent_name)
                    // .size(0,0)
                    // .text(this.session_service.agent_code)
                    // .feed(2)
                    // .align(escpos.TextAlignment.Center)
                    // .image(qr,escpos.BitmapDensity.D24)
                    // .feed(2)
                    .generateUInt8Array() 
                    this.bprinter.sendToBluetoothPrinter(this.session_service.printer_mac_address,escposCommands)

                })

      
            }

        })

    } 

    place_bet() {

        this.print(this.draw_category,this.bets)
       //  return
        this.presentLoadingWithOptions().then(() => {  

            this.betting_service.place_bet(this.bets,this.draw_category).subscribe((response: any) => {

                this.loading.dismiss() 

                this.presentToastWithOptions('BETTING EXPRESS - PLACE BETS',response.message,(response.status == true? 'primary':'danger'))
                
                if(response.status === false) {
                    return  
                }

                this.bets = []
                let tmp_amt = this.cost
                this.wallet_service.use_wallet(tmp_amt) 
                this.cost = 0
                console.log(response)

            })

        })
        
    }

    qr_bet_string(bets: Bet[]) {

        let bet_string = "";

        bets.forEach((bet) => {
            
            let append_value = bet.number + "-" + bet.straight + "R" + bet.ramble
            
            if(bet_string != "") {
                bet_string += "," + append_value 
            } else {
                bet_string += append_value
            }

        })

        return bet_string

    }

    to_3d_bet_string(bets: Bet[]) {

        let bet_string = "";

        bets.forEach((bet) => {
            
            let append_value = bet.number + "-" + bet.straight + "R" + bet.ramble
            
            if(bet_string != "") {
                bet_string += "<br/>" + append_value 
            } else {
                bet_string += append_value
            }

        })

        return bet_string

    }

    add_to_list() {

        if(this.number == '' || (this.ramble == 0 && this.straight == 0) || this.number == null || this.ramble == null || this.straight == null) {
            return 
        }

        let not_number = Number.isNaN(Number.parseInt(this.number))
        if(not_number) {
            return
        }

        if(this.number.length < 3) {
            return
        }

        if(this.bets.length + 1 > 10) {
            alert('Cannot add more, each batch can only have 9 bet numbers')
            return
        }

        // let tmptotal = this.cost + parseInt(this.straight.toString()) + parseInt(this.ramble.toString())

        // if(tmptotal > this.wallet_balance) {
        //     alert('Cannot add bet, you do not have enough balance, try refreshing your wallet, or cash-in to your wallet')
        //     return
        // }

        let bet: Bet = {
            draw: this.draw,
            number: this.number,
            ramble: this.ramble,
            straight: this.straight
        }

        this.cost += parseInt(this.straight.toString()) + parseInt(this.ramble.toString())
        this.bets.push(bet)
        this.straight = null
        this.ramble = null
        this.number = null
        this.is_send_button_disabled = false

    }

    remove_bet(bet_to_remove) {

        let newbets = this.bets.filter((bet,i,arr) => {
            return bet != bet_to_remove
        })

        this.cost -= parseInt(bet_to_remove.straight.toString()) + parseInt(bet_to_remove.ramble.toString())
        this.bets = newbets

        if(this.bets.length < 1) {
            this.is_send_button_disabled = true
        }

    }

    update_field(key: string, target: any) {
        this[key] = target.value
    }

    dismiss() {
        this.modalController.dismiss({
            'dismissed': true
        });
    }

}

